var dropdown = $(K('field[Organization Name]').wrapper()).first();
$(dropdown).find('input').wrap($('<div class="input-group"/>'));

K('bridgedResource[Organizations]').load({
    attributes: ['Full Name'],
    success: function(bridgedData) {
        var tiers = {};
        var html = $('<div class="input-group-btn">' +
                        '<button class="btn btn-default dropdown-toggle" type="button" data-submenu data-toggle="dropdown">Select Organization</button>' + 
                        '<ul class="dropdown-menu top-level"/>' + 
                    '</div>');
        // Build Map of Dropdown Menu Data
        _.each(bridgedData,function(item)
           {
               var firstKey = item['Center'];
               var secondKey = item['Organization'];
               var thirdKey = item['D Code'];
               tiers[firstKey] = tiers[firstKey] || {};
               tiers[firstKey][secondKey] = tiers[firstKey][secondKey] || {};
               tiers[firstKey][secondKey][thirdKey] = tiers[firstKey][secondKey][thirdKey] || {};
           }
        );

        // Loop over tier 1's
        _.each(Object.keys(tiers),function(tier1){
            var tier1Html = $('<li class="dropdown dropdown-submenu">' + 
                                '<a tabindex="-1" href="#" class="dropdown-toggle" data-toggle="dropdown">' + tier1 + '</a>' + 
                                '<ul tier-1="'+tier1+'" class="dropdown-menu"/>');
            // Loop over tier 2's
            _.each(Object.keys(tiers[tier1]), function(tier2){
                var tier2Html = $('<li class="dropdown dropdown-submenu">' + 
                                '<a tabindex="-1" href="#" class="dropdown-toggle" data-toggle="dropdown">' + tier2 + '</a>' + 
                                '<ul tier-2="'+tier2+'" class="dropdown-menu"/>');

                // Loop over tier3's
                _.each(Object.keys(tiers[tier1][tier2]), function(tier3){
                    var tier3Html = $('<li><a class="tier3Org" href="#">' + tier3 + '</a></li>');
                    $(tier2Html).find('ul[tier-2="'+tier2+'"]').append(tier3Html);
                })

                $(tier1Html).find('ul[tier-1="'+tier1+'"]').append(tier2Html);
            })
            $(html).find('ul.top-level').append(tier1Html);
        })
        $(dropdown).find('.input-group').prepend(html);

        // Call the dropdown-submenu JS library
        $('[data-submenu]').submenupicker();

        $('.tier3Org').click(function(e){
            e.preventDefault();
            tier2 = $(this).parent().parent().parent().children('a').text()
            tier1 = $(this).parent().parent().parent().parent().parent().children('a').text();
            val = tier1 + "::" + tier2 + "::" + $(this).text();
            K('field[Organization Name]').value(val);
        })
    }
});
